import React, { useState } from 'react';
import { Formik, Form, Field, ErrorMessage} from 'formik';
import { useDispatch } from 'react-redux';
import { addTool} from '../slices/toolSlice';
import * as Yup from 'yup';
import { useSelector } from 'react-redux';



function ToolForm() {

  const dispatch = useDispatch();
  const [selectedCategory, setSelectedCategory] = useState('')
  const validationSchema = Yup.object({
    name: Yup.string().required('Name is required'),
    category_id: Yup.number().required('Category is required'), 
    serial: Yup.string().required('Serial is required'),
    description: Yup.date().required('Description is required'),
    model: Yup.date().required('Model is required'),
  });



  const tools = useSelector(state => state.tools.tools);
  const uniqueCategories = tools
  .map(tool => tool.category) 
  .filter(category => category !== null && category !== undefined) 
  .reduce((acc, category) => {
    // Check if the category id is already in the accumulator
    const exists = acc.some(item => item.id === category.id);
    if (!exists) {
      // If it does not exist, add the category to the accumulator
      acc.push(category);
    }
    return acc;
  }, []); // Initialize the accumulator as an empty array
  console.log('uniqueCategories',uniqueCategories)


  // Initial values for the form
  const initialValues = {
    name: '',
    category_id: '', 
    serial: '',
    description: '',
    model:'',
  };


  // onSubmit function with Redux dispatch
  const onSubmit = (values, { setSubmitting, resetForm }) => {
    console.log('Submitting Form with Values:', values); // Debug log


    // Create the payload to send to the server
    const payload = {
      name: values.name,
      category_id: parseInt(values.category_id, 10), 
      serial: values.serial,
      description: values.description,
      model: values.model,
    };

    console.log('Attempting to submit form with values:', payload);


    dispatch(addTool(payload))
  };


  return (
    <div className="p-4 bg-base-100">
      <h3 className="text-lg font-semibold">Add an tool!</h3>
      <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
        {({ isSubmitting, setFieldValue }) => (
          <Form className="form-control w-full max-w-xs">
            <label className="label" htmlFor="category_id">
            <span className="label-text">Job Class</span>
            </label>
            <Field as="select" name="category_id" className="select select-bordered w-full max-w-xs" onChange={e => {
              const value = e.target.value;
              setFieldValue("category_id", value);
              setSelectedCategory(value);
            }}>
              <option value="">Select a Job Class</option>
              {uniqueCategories.map(category => (
                <option key={category.id} value={category.id}>{category.name}</option>
              ))}
            </Field>
            <ErrorMessage name="category_id" component="div" className="text-error" />

            {/* Always visible fields */}
            <Field name="name" placeholder="Name" className="select select-bordered w-full max-w-xs"/>
            <ErrorMessage name="name" component="div" className="text-error" />


            {/* Conditional Fields based on category_id */}
            {selectedCategory && (
              <>
                <Field name="model" placeholder="Model" className="select select-bordered w-full max-w-xs"/>
                <ErrorMessage name="model" component="div" className="text-error" />

                <Field name="serial" placeholder="Serial" className="select select-bordered w-full max-w-xs"/>
                <ErrorMessage name="serial" component="div" className="text-error" />

                <Field name="status" placeholder="Status" className="select select-bordered w-full max-w-xs"/>
                <ErrorMessage name="status" component="div" className="text-error" />

                {selectedCategory === '2' && (
                  <>
                    <Field name="casesPerHour" placeholder="Cases Per Hour" className="select select-bordered w-full max-w-xs"/>
                    <ErrorMessage name="casesPerHour" component="div" className="text-error" />
                  </>
                )}

                {['1', '3', '4', '5'].includes(selectedCategory) && (
                  <>
                    <Field name="palletsPerHour" placeholder="Pallets Per Hour" className="select select-bordered w-full max-w-xs"/>
                    <ErrorMessage name="palletsPerHour" component="div" className="text-error" />
                  </>
                )}


                <Field name="hireDate" type="date" className="select select-bordered w-full max-w-xs" />
                <ErrorMessage name="hireDate" component="div" className="text-error" />
              </>
            )}

            <button className="btn btn-primary mt-4" type="submit" disabled={isSubmitting}>Submit</button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default ToolForm;
