import React from 'react';
import { useNavigate, Link, Routes, Route, Outlet } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { logoutUser } from '../slices/authSlice';
import MasterOperatingPlan from './MasterOperatingPlan';
import ExecutiveSummary from './ExecutiveSummary';
import ManageTestLines from './ManageTestLines';
import ManageUsers from './ManageUsers';
import Breadcrumbs from './Breadcrumbs';

function Layout() {
  const auth = useSelector((state) => state.auth);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutUser()).then(() => {
      console.log('Logout successful');
      navigate('/login');
    }).catch((error) => console.error('Logout failed', error));
  };

  return (
    <div className="flex h-screen">
      <aside className="w-64 bg-gray-800 text-white flex flex-col">
        <div className="p-4 text-2xl font-bold flex justify-center">
          <img src="/nokialogo.png" alt="Nokia Logo" style={{ width: '70%', height: 'auto' }} />
        </div>
        <nav className="flex-grow">
          <ul className="space-y-2">
            <li className="hover:bg-gray-700">
              <Link to="/masteroperatingplan" className="block px-4 py-2">Manage All Tools</Link>
            </li>
            <li className="hover:bg-gray-700">
              <Link to="/manage-test-lines" className="block px-4 py-2">Manage All Test Lines</Link>
            </li>
            {auth.user && auth.user.role === 'admin' && (
              <li className="hover:bg-gray-700">
                <Link to="/manage-users" className="block px-4 py-2">Manage Users</Link>
              </li>
            )}
            <li className="hover:bg-gray-700">
              <button onClick={handleLogout} className="block px-4 py-2 w-full text-left">Logout</button>
            </li>
          </ul>
        </nav>
      </aside>
      <div className="flex-1">
        <header className="bg-gray-100 p-4">
          <div className="flex justify-between items-center">
            <h1 className="text-2xl font-bold">Dashboard</h1>
            <div className="text-gray-700">Nokia NDAC</div>
          </div>
        </header>
        <main className="p-4">
          <Breadcrumbs />
          <Outlet />
        </main>
        <footer className="bg-gray-100 p-4 text-center">
          <p>Â© 2024 Nokia One Lab</p>
        </footer>
      </div>
    </div>
  );
}

export default Layout;
