import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { useDispatch } from 'react-redux';
import { addCategory } from '../slices/toolSlice';
import * as Yup from 'yup';
import { useSelector } from 'react-redux';

function CategoryForm() {
  const dispatch = useDispatch();

  const validationSchema = Yup.object({
    name: Yup.string().required('Name is required'),
    user_id: Yup.number().required('User ID is required'),
  });

  const initialValues = {
    name: '',
    user_id: '',
  };

  const onSubmit = (values, { setSubmitting, resetForm }) => {
    console.log('Submitting Form with Values:', values); // Debug log

    const payload = {
      name: values.name,
      user_id: parseInt(values.user_id, 10),
    };

    console.log('Attempting to submit form with values:', payload);

    dispatch(addCategory(payload));
    setSubmitting(false);
    resetForm();
  };

  return (
    <div className="p-4 bg-base-100">
      <h3 className="text-lg font-semibold">Add a Category!</h3>
      <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
        {({ isSubmitting }) => (
          <Form className="form-control w-full max-w-xs">
            <label className="label" htmlFor="name">
              <span className="label-text">Category Name</span>
            </label>
            <Field name="name" placeholder="Name" className="select select-bordered w-full max-w-xs" />
            <ErrorMessage name="name" component="div" className="text-error" />

            <label className="label" htmlFor="user_id">
              <span className="label-text">User ID</span>
            </label>
            <Field name="user_id" placeholder="User ID" className="select select-bordered w-full max-w-xs" />
            <ErrorMessage name="user_id" component="div" className="text-error" />

            <button className="btn btn-primary mt-4" type="submit" disabled={isSubmitting}>Submit</button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default CategoryForm;
