import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUsers, deleteUser, addUser } from '../slices/authSlice';
import UserForm from './UserForm'; // Assume UserForm is a form for adding users

const ManageUsers = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.auth.users);
  const userStatus = useSelector((state) => state.auth.userStatus);

  useEffect(() => {
    if (userStatus === 'idle') {
      dispatch(fetchUsers());
    }
  }, [userStatus, dispatch]);

  const handleDelete = (userId) => {
    dispatch(deleteUser(userId));
  };

  const handleAddUser = (userData) => {
    dispatch(addUser(userData));
  };

  return (
    <div>
      <h2>Manage Users</h2>
      <UserForm onSubmit={handleAddUser} />
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Username</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <button onClick={() => handleDelete(user.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageUsers;
