import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { deleteTool, updateTool } from '../slices/toolSlice';
import { useFormik } from 'formik';
import * as Yup from 'yup';

const ToolCard = ({ tool, handleDelete }) => {
  const dispatch = useDispatch();
  const [editMode, setEditMode] = useState(false);

  const validationSchema = Yup.object({
    name: Yup.string().required('Name is required'),
    description: Yup.string().required('Description is required'),
    category: Yup.string().required('Category is required'),
    status: Yup.string().required('Status is required'),
    serial: Yup.string().required('Serial is required'),
  });

  const formik = useFormik({
    initialValues: {
      name: tool.name || '',
      description: tool.description || '',
      category: tool.category.name || '',
      status: tool.status || '',
      serial: tool.serial || '',
    },
    validationSchema,
    onSubmit: (values) => {
      const updatedData = {
        toolId: tool.id,
        ...values,
      };
      console.log('Dispatching updateTool with data:', updatedData);
      dispatch(updateTool(updatedData))
        .unwrap()
        .then(() => {
          setEditMode(false); // Turn off edit mode on successful update
        })
        .catch(error => {
          console.error('Error updating tool:', error);
        });
    },
  });

  return (
    <div className="card bg-base-100 shadow-xl">
      {editMode ? (
        <form onSubmit={formik.handleSubmit} className="card-body">
          <div className="form-control">
            <label className="label">
              <span className="label-text">Name</span>
            </label>
            <input
              type="text"
              className="input input-bordered"
              {...formik.getFieldProps('name')}
            />
            {formik.errors.name && <div>{formik.errors.name}</div>}
          </div>
          <div className="form-control">
            <label className="label">
              <span className="label-text">Description</span>
            </label>
            <input
              type="text"
              className="input input-bordered"
              {...formik.getFieldProps('description')}
            />
            {formik.errors.description && <div>{formik.errors.description}</div>}
          </div>
          <div className="form-control">
            <label className="label">
              <span className="label-text">Category</span>
            </label>
            <input
              type="text"
              className="input input-bordered"
              {...formik.getFieldProps('category')}
            />
            {formik.errors.category && <div>{formik.errors.category}</div>}
          </div>
          <div className="form-control">
            <label className="label">
              <span className="label-text">Status</span>
            </label>
            <input
              type="text"
              className="input input-bordered"
              {...formik.getFieldProps('status')}
            />
            {formik.errors.status && <div>{formik.errors.status}</div>}
          </div>
          <div className="form-control">
            <label className="label">
              <span className="label-text">Serial</span>
            </label>
            <input
              type="text"
              className="input input-bordered"
              {...formik.getFieldProps('serial')}
            />
            {formik.errors.serial && <div>{formik.errors.serial}</div>}
          </div>
          <div className="card-actions justify-end">
            <button type="submit" className="btn btn-primary">Save</button>
            <button type="button" onClick={() => handleDelete(tool.id)} className="btn btn-error">Delete</button>
            <button type="button" onClick={() => setEditMode(false)} className="btn btn-secondary">Cancel</button>
          </div>
        </form>
      ) : (
        // View mode displaying tool information
        <div className="card-body">
          <h2 className="card-title">{tool.name}</h2>
          <p><strong>Description:</strong> {tool.description}</p>
          <p><strong>Category:</strong> {tool.category.name}</p>
          <p><strong>Status:</strong> {tool.status}</p>
          <p><strong>Serial:</strong> {tool.serial}</p>
          <div className="card-actions justify-end">
            <button onClick={() => setEditMode(true)} className="btn btn-primary">Edit</button>
            <button onClick={() => handleDelete(tool.id)} className="btn btn-error">Delete</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ToolCard;
