import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { logoutUser } from '../slices/authSlice';
import ExecutiveSummary from './ExecutiveSummary';

function Layout() {
  const auth = useSelector((state) => state.auth);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logoutUser())
      .then(() => {
        console.log('Logout successful');
      })
      .catch((error) => console.error('Logout failed', error));
  };

  return (
    <div className="flex flex-col h-screen">
      <header className="navbar bg-base-100" style={{ zIndex: '1000' }}>
        <div className="flex-grow">
          <div className="flex justify-between items-center w-full px-4">
            <Link to="/masteroperatingplan">
              <button className="btn btn-primary btn-lg text-xl flex justify-center items-center">
                Manage All Tools
              </button>
            </Link>
            <Link to="/manage-test-lines">
              <button className="btn btn-primary btn-lg text-xl flex justify-center items-center">
                Manage All Test Lines
              </button>
            </Link>
            {!auth.isAuthenticated ? (
              <>
                <Link to="/login"><button className="btn">Login</button></Link>
                <Link to="/signup"><button className="btn">Signup</button></Link>
              </>
            ) : (
              <button onClick={handleLogout} className="btn btn-info btn-lg text-xl flex justify-center items-center">
                Logout
              </button>
            )}
          </div>
        </div>
      </header>
      <ExecutiveSummary />
      <footer className="footer bg-base-300 text-base-content">
        <div className="items-center grid-flow-col">
          <p>Â© 2024 Nokia One Lab</p>
        </div>
      </footer>
    </div>
  );
}

export default Layout;
